import{_ as a}from"./plugin-vue_export-helper-c27b6911.js";import{o as e,c as t,f as r}from"./app-ad0845b4.js";const i="/assets/image-2023-01-26_16-35-59-965-06-输入法-39e07a26.png",n={},c=r('<h1 id="数字键盘" tabindex="-1"><a class="header-anchor" href="#数字键盘" aria-hidden="true">#</a> 数字键盘</h1><h2 id="场景1" tabindex="-1"><a class="header-anchor" href="#场景1" aria-hidden="true">#</a> 场景1</h2><p>输入法一定是安全的吗？在之前也曾曝出不少输入法搜集用户信息获取照片权限，通讯录权限，等。</p><p>试想一下当你使用某种聊天工具，在跟朋友探讨一件商品，过了一会儿，在某度，某宝，某东可能就推荐了。</p><h2 id="场景2" tabindex="-1"><a class="header-anchor" href="#场景2" aria-hidden="true">#</a> 场景2</h2><p>在我们使用微信，支付宝，等支付软件付款的时候，发现键盘是App 自带的，并非第三方输入法</p><p>这个是由于第三方软件输入的频次过多他会把密码记录下来，其二就是如果三方输入法被木马入侵，也容易盗取我们的密码造成损失。</p><p>所以在我们开发移动端的时候遇到支付都会使用虚拟键盘</p><h2 id="vant组件-数字键盘" tabindex="-1"><a class="header-anchor" href="#vant组件-数字键盘" aria-hidden="true">#</a> vant组件-数字键盘</h2><figure><img src="'+i+'" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure>',10),d=[c];function h(_,s){return e(),t("div",null,d)}const f=a(n,[["render",h],["__file","06-输入法-虚拟键盘.html.vue"]]);export{f as default};
